(function(tinymce){tinymce.PluginManager.requireLangPack("imgInsert");tinymce.create('tinymce.plugins.imgInsertPlugin',{init:function(ed,url){ed.addCommand('imgInsertControl',function(){ed.windowManager.open({file:url+'/imageInsert.php',width:250+parseInt(ed.getLang('emotions.delta_width',0)),height:160+parseInt(ed.getLang('emotions.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('imgInsert',{title:'imgInsert.title',cmd:'imgInsertControl',image:url+'/img/document_picture.png'})},getInfo:function(){return{longname:'Emotions',author:'Moxiecode Systems AB',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/emotions',version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add('imgInsert',tinymce.plugins.imgInsertPlugin)})(tinymce);