(function(tinymce){tinymce.PluginManager.requireLangPack('insertAttachment');tinymce.create('tinymce.plugins.insertAttachmentPlugin',{init:function(ed,url){ed.addCommand('insertAttachmentControl',function(){ed.windowManager.open({file:url+'/attachment.jsp',width:350,height:200,inline:1},{plugin_url:url})});ed.addButton('insertAttachment',{title:'insertAttachment.title',cmd:'insertAttachmentControl',image:url+'/img/cv.png'})},getInfo:function(){return{longname:'attachment',author:'Moxiecode Systems AB',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/emotions'}}});tinymce.PluginManager.add('insertAttachment',tinymce.plugins.insertAttachmentPlugin)})(tinymce);