(function(tinymce){tinymce.PluginManager.requireLangPack('insertImg');tinymce.create('tinymce.plugins.insertImgPlugin',{init:function(ed,url){ed.addCommand('insertImgControl',function(){ed.windowManager.open({file:url+'/attachment.jsp',width:350,height:170,inline:1},{plugin_url:url})});ed.addButton('insertImg',{title:'insertImg.title',cmd:'insertImgControl',image:url+'/img/document_picture.png'})},getInfo:function(){return{longname:'insertImg',author:'Moxiecode Systems AB',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/emotions'}}});tinymce.PluginManager.add('insertImg',tinymce.plugins.insertImgPlugin)})(tinymce);